import Meta from "./Meta";
import Header from "./Header";
import Footer from "./Footer";

const Layout = props => (
  <>
    <Meta />
    <div className="main-container">
      <Header />
      <main>{props.children}</main>
      <Footer />
    </div>
    <style jsx>{`
      .main-container {
        max-width: 1400px;
        margin: 0 auto;
        height: 100%;

        display: flex;
        flex-direction: column;
      }

      main {
        flex: 1;
        margin: 1rem;
        width: 1200px;
        max-width: 1200px;
        align-self: center;
        background: white;
      }
    `}</style>
  </>
);

export default Layout;
